	public static function convert(array $in)
	{
		/* Determine initial string length */
		$in_length = count($in);
		$out = [];

		/* Loop over input array */
		for ($i = 0; $i < $in_length; $i++) {
			$block = $in[$i] >> 8;
			$cp    = $in[$i] & 255;

			$no_jump = 0;
			switch ($block) {
				case 0: $jump_map = self::$jump_table[0]; $replace_map = self::$map_table[0]; break;
				case 32: $jump_map = self::$jump_table[1]; $replace_map = self::$map_table[1]; $expand_map = self::$expand_table[0]; break;
				case 2: $jump_map = self::$jump_table[2]; $replace_map = self::$map_table[2]; break;
				case 39: $jump_map = self::$jump_table[3]; $replace_map = self::$map_table[3]; break;
				case 48: $jump_map = self::$jump_table[4]; $replace_map = self::$map_table[4]; break;
				case 38: $jump_map = self::$jump_table[5]; $replace_map = self::$map_table[5]; break;
				default: $no_jump = 1;
			}
			if ($no_jump) {
				$jump = 0;
			} else {
				$jump = $jump_map[$cp];
			}

			switch ($jump) {
				case 0: /* No changes */
					$out[] = $in[$i];
					break;
				case 1: /* Simple mapping */
					$out[] = $replace_map[$cp];
					break;
				case 2: /* Expand to more than one char */
					for ($j = 1; $j <= $expand_map[$cp][0]; $j++) {
						$out[] = $expand_map[$cp][$j];
					}
					break;
				case 3: /* Skip */
					break;
			}
		}
		return $out;
	}
